export const initialState = {
    isLoading: null,
    error: null,
    customerList: [],
    customer:{
      customerName: '',
      customerGroup: ''
    }
  }


export const customerData = () => ({type: 'CUSTOMER_LIST'});
export const saveCustomer1 = () => ({type: 'SAVE_CUSTOMER'});
// export const addCustomer = (data) => ({type: 'ADD_CUSTOMER' , payload: data});
export const updateCustomer = () => ({type: 'UPDATE_CUSTOMER'});
export const deleteCustomer = () => ({type: 'DELETE_CUSTOMER'})
export const CustomerListWorker = (state,action)=> {

  try {
    switch (action.type) {
      case 'CUSTOMER_LIST': {
        const data = Customer.getCustomerList();

        console.log('state', state)
        // return {
        //     ...state,
        //     customerList: data,
        //     isLoading: false
        // }
       return  fetchCustomerListSuccess({customerData: data})
        //yield put(fetchCustomerListSuccess({ customerData: data }));
        break;
      }

      // case 'ADD_CUSTOMER': {
      //   return {
      //     ...initialState,
      //     isLoading: true,
      //     customer: action.payload  
      //   }
      //   break;
      // }

      case 'SAVE_CUSTOMER': {
          console.log('add customer', state)
       const { customer } = state
        if (!(customer && customer.customerGroup && customer.customerGroup.trim())) {
          alert('Customer group should not be empty!');
          return;
        }

        if (!(customer && customer.customerName && customer.customerName.trim())) {
          alert('Customer name should not be empty!');
          return;
        }

          return fetchCustomerListAddSuccess({ 
          newCustomerData: {
            model: "accounts_receivable.cusgrp",
            pk: uuidv4(),
            fields: {
              customer_group: customer.customerGroup,
              csgrp_name: customer.customerName
            }
          }}
        );
        // yield put(resetAddCustomerState());
        break;
      }
      case 'UPDATE_CUSTOMER': {
          console.log('update customer', state)
          const {id, newCustomerName} = action.payload;
        if (!(newCustomerName && newCustomerName.trim())) {
          alert('Customer name should not be empty!');
          return;
        }
        let {customerList} = state;
        const clonedCustomerList = JSON.parse(JSON.stringify(customerList));
        const index = clonedCustomerList.findIndex((customer) => customer.pk === id);
        clonedCustomerList[index].fields.csgrp_name = newCustomerName;
        // yield put(updateCustomerList({ customerList: clonedCustomerList }));
      return  updateCustomerList({ customerList: clonedCustomerList })
        // Use custom delay
        // yield delay(600);
        // alert('Customer name has been successfully updated!');
        break;
      }
      case 'DELETE_CUSTOMER': {
        const {id} = action.payload;
        const {customerList} = state;
        const clonedCustomerList = JSON.parse(JSON.stringify(customerList));
        const updatedCustomerList = clonedCustomerList.filter((customer) => customer.pk !== id);
       return updateCustomerList({ customerList: updatedCustomerList });
        // // Use custom delay
        // yield delay(600);
        // alert('Customer has been successfully deleted!');
        break;
      }
      default:
        return {
          ...state
        }
        break
    }
  } catch (e) {
    console.error(`Error occuring while calling an action ${action.type}`, e);
  }
}

