import React,{useState, createContext, useReducer, useContext} from 'react';

export const CustomerContext = createContext();
export const useStoreContext = () => useContext(CustomerContext); //creating our own useContext hook

export const CustomerDataProvider = ({children, initialState, initialStateAddCustomer, reducer}) =>{

    // initilize all the state data here
    console.log('initial states', initialStateAddCustomer)
    const initialStates = {
        initialState: initialState,
        initialStateAddCustomer: initialStateAddCustomer
    }

    const [globalState, dispatch] = useReducer(reducer, initialStates)

    return (
        <CustomerContext.Provider value={[globalState, dispatch]}>{children}</CustomerContext.Provider>
    );
}
