   export const addCustomerAction = (data) => ({type: 'Add_customer' , payload: data});
   export const saveCustomer = (data) => ({type: 'SAVE_CUSTOMER', payload: data});
  export const addCustomer = (state=initialState,action) => 
  {
    console.log('payload', action)
      switch(action.type){
        case 'Add_customer':
            return {
              ...initialState,
              isLoading: true,
              customer: action.payload  
            }
            break;

        case 'SAVE_CUSTOMER':

            const {customer} = state;
            return fetchCustomerListAddSuccess({ 
              newCustomerData: {
                model: "accounts_receivable.cusgrp",
                pk: uuidv4(),
                fields: {
                  customer_group: customer.customerGroup,
                  csgrp_name: customer.customerName
                }
              }})
              break;

          default:
            return state;
      }
    }

  export const resetAddCustomerState= (state, action) => ({
      ...initialState
    }); 






