import React, { useState,useReducer } from "react"
import { createContainer } from "unstated-next"


export const initialState = {
    isLoading: null,
    error: null,
    customerList: [],
  }

  export const customerData = () => ({type: 'CUSTOMER_LIST'});
export const saveCustomer1 = (data) => ({type: 'SAVE_CUSTOMER', payload: data});
// export const addCustomer = (data) => ({type: 'ADD_CUSTOMER' , payload: data});
export const updateCustomer = () => ({type: 'UPDATE_CUSTOMER'});
export const deleteCustomer = () => ({type: 'DELETE_CUSTOMER'})
const reducerOne = (state, action) =>{
    switch (action.type) {
        case 'increment':
          return {count: state.count + 1};
        case 'decrement':
          return {count: state.count - 1};
        case 'reset':
          return action.payload;
        default:
          throw new Error();
      }
}

function useCounter(){
    const [state, dispatch] = useReducer(reducerOne, initialCount);
    return {state, dispatch}
}


export const Counter  = createContainer(useCounter);
