import { useReducer, useCallback } from 'react'
import {initialState as initialStateOne,CustomerListWorker as reducerOne} from './contextReducer';
import {initialState as initialStateTwo, addCustomer as reducerTwo} from './add-customer';
import combineReducers from 'react-combine-reducers';

export const [reducerCombined, initialStateCombined] = useCallback(()=>{
  combineReducers({
  reducerOne: [reducerOne, initialStateOne],
  reducerTwo: [reducerTwo, initialStateTwo],
  // ...
})},[reducerCombined, initialStateCombined]);
